// Helper functions for filtering
export const getCarsByCountry = (countryCode: string): Car[] => {
  return carsData.filter(car => car.countryCode === countryCode);
};

export const getCarsByBrand = (brand: string): Car[] => {
  return carsData.filter(car => car.brand === brand);
};

export const getCarsByCategory = (category: string): Car[] => {
  return carsData.filter(car => car.category.toLowerCase() === category.toLowerCase());
};

export const getFeaturedCars = (): Car[] => {
  return carsData.filter(car => car.isFeatured);
};

export const getNewCars = (): Car[] => {
  return carsData.filter(car => car.isNew);
};

export const getCarById = (id: string): Car | undefined => {
  return carsData.find(car => car.id === id);
};

export const getBrandsByCountry = (countryCode: string): string[] => {
  const countryBrands = carsData
    .filter(car => car.countryCode === countryCode)
    .map(car => car.brand);
  return [...new Set(countryBrands)];
};

export const getAllBrands = (): string[] => {
  const allBrands = carsData.map(car => car.brand);
  return [...new Set(allBrands)];
};
